import PropTypes from 'prop-types';

const getColorValue = (color) => {
  switch (color) {
    case 'primary':
      return 'var(--primary)';
    case 'black':
      return 'black';
    case 'white':
      return 'white';
    default:
      return 'var(--primary)';
  }
};

