/**
 * Logo component with icon and text parts
 * @typedef {Object} LogoProps
 * @property {'default' | 'icon' | 'text'} [variant='default'] - Variant of the logo to display
 * @property {'primary' | 'black' | 'white'} [color='primary'] - Color of the logo
 * @property {number} [iconWidth] - Width of the icon (only used when variant includes icon)
 * @property {number} [iconHeight] - Height of the icon (only used when variant includes icon)
 * @property {number} [textWidth] - Width of the text (only used when variant includes text)
 * @property {number} [textHeight] - Height of the text (only used when variant includes text)
 */
const Logo = ({ variant = 'default', color = 'primary', iconWidth, iconHeight, textWidth, textHeight, style, ...props }) => {
  const renderIcon = variant === 'default' || variant === 'icon';
  const renderText = variant === 'default' || variant === 'text';
  const fillColor = getColorValue(color);

  const containerStyle = {
    display: 'inline-flex',
    alignItems: 'center',
    gap: variant === 'default' ? '16px' : 0,
    ...style,
  };

  return (
    <div style={containerStyle} {...props}>
      {renderIcon && <IconSVG width={iconWidth} height={iconHeight} fill={fillColor} />}
      {renderText && <TextSVG width={textWidth} height={textHeight} fill={fillColor} style={variant === 'default' ? { marginTop: '2%' } : undefined} />}
    </div>
  );
};

Logo.propTypes = {
  variant: PropTypes.oneOf(['default', 'icon', 'text']),
  color: PropTypes.oneOf(['primary', 'black', 'white']),
  iconWidth: PropTypes.number,
  iconHeight: PropTypes.number,
  textWidth: PropTypes.number,
  textHeight: PropTypes.number,
  style: PropTypes.object,
};

export default Logo;